// jobhunt-frontend/src/JobSubmission.js

import React, { useState } from 'react';
import Modal from './Modal';
import './Modal.css';
import './JobSubmission.css';

function JobSubmission() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here, you can perform any necessary actions with the job details, such as sending a request to your backend API
    console.log('URL:', url);
    console.log('Job Title:', title);
    console.log('Company:', company);
    closeModal();
  };

  return (
    <div className="JobSubmissionContainer">
      <button className="job-submission-button" onClick={openModal}>Jobs Applied</button>
      {isModalOpen && (
        <Modal closeModal={closeModal}>
          <form onSubmit={handleSubmit}>
            <label>
              Job URL:
              <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} required />
            </label>
            <label>
              Job Title:
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </label>
            <label>
              Company:
              <input type="text" value={company} onChange={(e) => setCompany(e.target.value)} required />
            </label>
            <div className="modal-actions">
              <button type="submit">Submit</button>
            </div>
          </form>
        </Modal>
      )}
    </div>
  );
}

export default JobSubmission;
