// jobhunt-frontend/src/JobSubmission.js

import React, { useState } from 'react';

function JobSubmission() {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');

  const openModal = () => {
    const modalWindow = window.open('', '_blank', 'width=400,height=300');
    modalWindow.document.title = 'Job Submission';
    modalWindow.document.body.style.display = 'flex';
    modalWindow.document.body.style.flexDirection = 'column';
    modalWindow.document.body.style.alignItems = 'center';
    modalWindow.document.body.innerHTML = `
      <h2>Job Submission</h2>
      <form id="jobForm">
        <label>
          Job URL:
          <input type="text" value="" required>
        </label>
        <label>
          Job Title:
          <input type="text" value="" required>
        </label>
        <label>
          Company:
          <input type="text" value="" required>
        </label>
        <button type="submit">Submit</button>
      </form>
    `;

    modalWindow.document.getElementById('jobForm').addEventListener('submit', (event) => {
      event.preventDefault();
      const formData = new FormData(modalWindow.document.getElementById('jobForm'));
      const submittedData = Object.fromEntries(formData.entries());
      console.log('Submitted Data:', submittedData);
      modalWindow.close();
    });
  };

  return (
    <div>
      <button onClick={openModal}>Jobs Applied</button>
    </div>
  );
}

export default JobSubmission;
