// jobhunt-frontend/src/ApplicationStatus.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function ApplicationStatus() {
  const [jobs, setJobs] = useState([
    { id: 1, url: 'http://job1.com', title: 'Job 1', company: 'Company 1', status: 'Applied' },
    { id: 2, url: 'http://job2.com', title: 'Job 2', company: 'Company 2', status: 'Interviewing' },
    // Add more jobs as needed
  ]);

  const updateStatus = (id, newStatus) => {
    setJobs(jobs.map(job => job.id === id ? { ...job, status: newStatus } : job));
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ marginRight: '10px' }}>Application Status</h2>
        <Link to="/">Go to Homepage</Link>
      </div>
      {jobs.map((job) => (
        <div key={job.id}>
          <h3>{job.title} at {job.company}</h3>
          <p>URL: <a href={job.url}>{job.url}</a></p>
          <p>Status: {job.status}</p>
          <label>
            Update Status:
            <select onChange={(e) => updateStatus(job.id, e.target.value)}>
              <option value="">Select...</option>
              <option value="Applied">Applied</option>
              <option value="Phone Screen">Phone Screen</option>
              <option value="Interviewing">Interviewing</option>
              <option value="Offered">Offered</option>
              <option value="Rejected">Rejected</option>
            </select>
          </label>
        </div>
      ))}
    </div>
  );
}

export default ApplicationStatus;
