// jobhunt-frontend/src/JobSubmission.js

import React, { useState } from 'react';

function JobSubmission() {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');

  const openModal = () => {
    const windowWidth = 400;
    const windowHeight = 300;
    const windowLeft = window.screen.width / 2 - windowWidth / 2;
    const windowTop = window.screen.height / 2 - windowHeight / 2;

    const modalWindow = window.open(
      '',
      '_blank',
      `width=${windowWidth},height=${windowHeight},left=${windowLeft},top=${windowTop},toolbar=no,location=no,status=no`
    );

    modalWindow.document.title = 'Job Submission';
    modalWindow.document.body.style.display = 'flex';
    modalWindow.document.body.style.flexDirection = 'column';
    modalWindow.document.body.style.alignItems = 'center';
    modalWindow.document.body.innerHTML = `
      <h2>Job Submission</h2>
      <form id="jobForm">
        <label>
          Job URL:
          <input type="text" value="" required>
        </label>
        <label>
          Job Title:
          <input type="text" value="" required>
        </label>
        <label>
          Company:
          <input type="text" value="" required>
        </label>
        <button type="button" id="submitButton">Submit</button>
      </form>
    `;

    modalWindow.document.getElementById('submitButton').addEventListener('click', () => {
      const urlInput = modalWindow.document.querySelector('input[type="text"]');
      const submittedData = {
        url: urlInput.value
      };
      console.log('Submitted Data:', submittedData);
      modalWindow.close();
    });
  };

  return (
    <div>
      <button onClick={openModal}>Jobs Applied</button>
    </div>
  );
}

export default JobSubmission;
