// jobhunt-frontend/src/ApplicationStatus.js

import React, { useState } from 'react';
import './ApplicationStatus.css';
import axios from 'axios';


function ApplicationStatus() {
  const [jobs, setJobs] = useState([
    // { id: 1, url: 'http://job1.com', title: 'Job 1', company: 'Company 1', status: 'Applied' },
    // { id: 2, url: 'http://job2.com', title: 'Job 2', company: 'Company 2', status: 'VO' },
    // { id: 3, url: 'http://job3.com', title: 'Job 3', company: 'Company 3', status: 'Rejected' },
    // Add more jobs as needed
  ]);

  const [filteredJobs, setFilteredJobs] = useState(jobs);
  const [filter, setFilter] = useState('');

  const updateStatus = (id, newStatus) => {
    fetch(`/api/jobs/${id}/update-status/`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status: newStatus }),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Job status updated:', data);
        // You can update the state or perform any additional actions after the status is successfully updated
      })
      .catch(error => {
        console.error('Error updating job status:', error);
      });
  };


  const filterJobs = (status) => {
    setFilter(status);
    if (status === 'No Response Yet') {
      setFilteredJobs(jobs.filter(job => job.status === 'Applied'));
    } else if (status === 'Interviewing') {
      setFilteredJobs(jobs.filter(job => job.status === 'Phone Screen' || job.status === 'VO'));
    } else if (status === 'Rejected') {
      setFilteredJobs(jobs.filter(job => job.status === 'Rejected'));
    } else {
      setFilteredJobs(jobs);
    }
  };

  return (
    <div className="application-status-container">
      <h2 className="application-status-heading">Application Status</h2>
      <div className="filter-buttons">
        <button className={filter === 'No Response Yet' ? 'active' : ''} onClick={() => filterJobs('No Response Yet')}>
          No Response Yet
        </button>
        <button className={filter === 'VO' ? 'active' : ''} onClick={() => filterJobs('Interviewing')}>
          Interviewing
        </button>
        <button className={filter === 'Rejected' ? 'active' : ''} onClick={() => filterJobs('Rejected')}>
          Rejected
        </button>
      </div>
      {filteredJobs.map((job) => (
        <div key={job.id}>
          <h3>{job.title} at {job.company}</h3>
          <p>URL: <a href={job.url}>{job.url}</a></p>
          <p>Status: {job.status}</p>
          <label>
            Update Status:
            <select onChange={(e) => updateStatus(job.id, e.target.value)}>
              <option value="">Select...</option>
              <option value="Applied">Applied</option>
              <option value="Phone Screen">Phone Screen</option>
              <option value="VO">VO</option>
              <option value="Offered">Offered</option>
              <option value="Rejected">Rejected</option>
            </select>
          </label>
        </div>
      ))}
    </div>
  );
}

export default ApplicationStatus;
