import React, { useState } from 'react';
import Modal from './Modal';
import axios from 'axios';
import './Modal.css';
import './JobSubmission.css';

function JobSubmission() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [error, setError] = useState('');
  const [submitMessage, setSubmitMessage] = useState('');

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!url || !title || !company) {
      setError('Missing values');
      return;
    }
    setError('');

    axios
      .post('http://localhost:8000/api/submit-job/', { url, title, company }) // Update the API URL here
      .then((response) => {
        if (response.data.error) {
          setError(response.data.error);
        } else {
          console.log('Job submitted successfully');
          closeModal();
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        setError('An error occurred while submitting the job');
      });
  };

  return (
    <div>
      <h2 className="application-status-heading">Job Hunting</h2>
      <div className="JobSubmissionContainer">
        <button className="job-submission-button" onClick={openModal}>
          Jobs Applied
        </button>
        {isModalOpen && (
          <Modal closeModal={closeModal}>
            <form onSubmit={handleSubmit}>
              <label>
                Job URL:
                <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} required />
              </label>
              <label>
                Job Title:
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
              </label>
              <label>
                Company:
                <input type="text" value={company} onChange={(e) => setCompany(e.target.value)} required />
              </label>
              {error && <p className="error-message">{error}</p>}
              {submitMessage && <p className="success-message">{submitMessage}</p>}
              <div className="modal-actions">
                <button type="submit">Submit</button>
              </div>
            </form>
          </Modal>
        )}
      </div>
      <ApplicationStatus /> {/* Assuming you have an `ApplicationStatus` component */}
    </div>
  );
}

export default JobSubmission;
