// jobhunt-frontend/src/JobSubmission.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function JobSubmission() {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('URL:', url);
    console.log('Job Title:', title);
    console.log('Company:', company);
    // In here, you will eventually make a POST request to your Django API
  };

  const goToStatusPage = () => {
    navigate('/status');
  };

  return (
    <div>
      <h2>Job Submission Form</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Job URL:
          <input type="text" value={url} onChange={e => setUrl(e.target.value)} required />
        </label>
        <label>
          Job Title:
          <input type="text" value={title} onChange={e => setTitle(e.target.value)} required />
        </label>
        <label>
          Company:
          <input type="text" value={company} onChange={e => setCompany(e.target.value)} required />
        </label>
        <button type="submit">Submit</button>
      </form>
      <button onClick={goToStatusPage}>Go to Application Status</button>
    </div>
  );
}

export default JobSubmission;
