import React, { useState } from 'react';
import Modal from './Modal';
import axios from 'axios';
import './Modal.css';
import './JobSubmission.css';

function JobSubmission() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [error, setError] = useState('');
  const [submitMessage, setSubmitMessage] = useState('');

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log('Submitting job:', { url, title, company });
    setError('');
    setSubmitMessage('');

    try {
      await axios.post('/api/submit-job/', { url, title, company });
      setSubmitMessage('Job submitted');
      // Clear the input fields
      setUrl('');
      setTitle('');
      setCompany('');
    } catch (error) {
      if (error.response && error.response.data) {
        setError(error.response.data.detail);
      } else {
        setError('An error occurred while submitting the job. Please try again.');
      }
    }
  };

  return (
    <div className="JobSubmissionContainer">
      <button className="job-submission-button" onClick={openModal}>
        Jobs Applied
      </button>
      {isModalOpen && (
        <Modal closeModal={closeModal}>
          <form onSubmit={handleSubmit}>
            <label>
              Job URL:
              <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} required />
            </label>
            <label>
              Job Title:
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </label>
            <label>
              Company:
              <input type="text" value={company} onChange={(e) => setCompany(e.target.value)} required />
            </label>
            <div className="modal-actions">
              <button type="submit">Submit</button>
            </div>
            {error && <p className="error-message">{error}</p>}
            {submitMessage && <p className="success-message">{submitMessage}</p>}
          </form>
        </Modal>
      )}
    </div>
  );
}

export default JobSubmission;
